import PropTypes from "prop-types";

//  Combine and sort deadlines from multiple sources (Jobs, Gigs, Events, Internships).
function combineAndSortDeadlines({ jobs = [], gigs = [], events = [], internships = [] }) {
    // Helper function: standardize (normalize) different deadline types
    const normalize = (item, type) => {
        // Pick the correct deadline field (since names differ per type)
        let deadline =
            item.applicationDeadline ||
            item.deliveryDeadline ||
            item.registrationDeadline ||
            null;

        // Default value (very large) so items without deadlines sort last
        let numericTime = Infinity;

        if (item.expired) {
            // If already expired, use the actual timestamp (expired ones come first)
            numericTime = deadline ? new Date(deadline).getTime() : 0;
        } else if (item.daysLeft !== undefined) {
            // Some items may have daysLeft directly (e.g., Gigs)
            numericTime = item.daysLeft;
        } else if (item.timeLeft && item.timeLeft !== "Expired" && item.timeLeft !== "No deadline") {
            // Parse "X days Y hours" into a numeric value (days + fraction of days)
            const match = item.timeLeft.match(/(\d+)\s*day.*?(\d+)\s*hour/);
            if (match) {
                const days = parseInt(match[1], 10);
                const hours = parseInt(match[2], 10);
                numericTime = days + hours / 24;
            }
        }

        // Return normalized item with a type label and numeric value for sorting
        return { ...item, type, numericTime };
    };

    // Merge all items into one array, tagging them by type
    const combined = [
        ...jobs.map((j) => normalize(j, "Job")),
        ...gigs.map((g) => normalize(g, "Gig")),
        ...events.map((e) => normalize(e, "Event")),
        ...internships.map((i) => normalize(i, "Internship")),
    ];

    // Sort logic:
    // 1. Expired items first
    // 2. Then items with the shortest time left
    const sorted = combined.sort((a, b) => {
        if (a.expired && !b.expired) return -1;
        if (!a.expired && b.expired) return 1;
        return a.numericTime - b.numericTime;
    });

    // Return only the first 5 results
    return sorted.slice(0, 5);
}

